import React from "react";

const Col = ({small, med, lg, children}) => (
    <div className={`${small.split(" ").map(small=>"col s" + small).join(" ")} ${med.split(" ").map(med=>"col m" + med).join(" ")} ${lg.split(" ").map(lg=>"col l" + lg).join(" ")} `}>
        {children}
    </div>

);

export default Col;
